function updateNavigationLinks(){const path=window.location.pathname;const pathParts=path.split('/');if(pathParts.length>=5&&!isNaN(pathParts[3])&&!isNaN(pathParts[4])){let year=parseInt(pathParts[3]);let month=parseInt(pathParts[4]);const currentDate=new Date();const currentYear=currentDate.getFullYear();const currentMonth=currentDate.getMonth()+1;let lastMonth=month-1;let lastMonthYear=year;if(lastMonth<1){lastMonth=12;lastMonthYear-=1}
let nextMonth=month+1;let nextMonthYear=year;if(nextMonth>12){nextMonth=1;nextMonthYear+=1}
document.getElementById('last-month').href=`/xaspos/talk/${lastMonthYear}/${String(lastMonth).padStart(2, '0')}`;if(!(year===currentYear&&month>=currentMonth)){document.getElementById('next-month').href=`/xaspos/talk/${nextMonthYear}/${String(nextMonth).padStart(2, '0')}`}else{document.getElementById('next-month').href='#';document.getElementById('next-month').style.pointerEvents='none'}}}
updateNavigationLinks()
