function gotoTicket(){var e=document.getElementById("date").value;var t=e.substring(0,4)+"/"+e.substring(5);window.location.href="/xaspos/talk/"+t}
var currentYear=new Date().getFullYear();var currentMonth=new Date().getMonth()+1;var yearDropdown=document.getElementById("date");yearDropdown.innerHTML="";var monthOptions=[];for(var year=2023;year<=currentYear;year++){var startMonth=(year===2023?9:1);var endMonth=(year===currentYear?currentMonth:12);for(var month=startMonth;month<=endMonth;month++){var option=document.createElement("option");var monthText=month<10?"0"+month:""+month;option.value=year+"-"+monthText;option.text=monthText+"/"+year;monthOptions.push(option)}}
for(var i=monthOptions.length-1;i>=0;i--){yearDropdown.appendChild(monthOptions[i])}
